const selectFields=document.querySelectorAll(".field--select");function closeAllSelects(e=null){selectFields.forEach(l=>{l!==e&&l.classList.remove("is-open")})}selectFields.length>0&&selectFields.forEach(e=>{e.querySelector("input").readOnly=!0,e.addEventListener("click",l=>{l.stopPropagation(),closeAllSelects(e),e.classList.toggle("is-open")})}),document.addEventListener("click",()=>{closeAllSelects()});